arguments.valueFrom:
${
  var cmd = [];
  if (inputs.input === null) {
     cmd.push('echo');
     cmd.push('no_inputs');
  } else {
    cmd.push('cat');
    if (Array.isArray(inputs.input)) {
        for (var i = 0; i < inputs.input.length; i++) {
           cmd.push(inputs.input[i].path);
        }
    } else {
      cmd.push(inputs.input.path);
    }
  }
  return cmd;
}
